nike.cssVariablesTemplate:
:root {
  /* Colors - Primary */
  --brand-color-primary-black: {{colors.primary.black}};
  --brand-color-primary-white: {{colors.primary.white}};

  /* Colors - Secondary */
  --brand-color-secondary-gray-dark: {{colors.secondary.gray-dark}};
  --brand-color-secondary-gray-medium: {{colors.secondary.gray-medium}};
  --brand-color-secondary-gray-light: {{colors.secondary.gray-light}};

  /* Colors - Accent */
  --brand-color-accent-orange: {{colors.accent.orange}};
  --brand-color-accent-red: {{colors.accent.red}};

  /* Typography */
  --brand-font-primary: {{typography.fontFamilies.primary}};
  --brand-font-secondary: {{typography.fontFamilies.secondary}};

  /* Spacing */
  --brand-spacing-xs: {{spacing.xs}};
  --brand-spacing-sm: {{spacing.sm}};
  --brand-spacing-md: {{spacing.md}};
  --brand-spacing-lg: {{spacing.lg}};
  --brand-spacing-xl: {{spacing.xl}};

  /* Border Radius */
  --brand-radius-sm: {{borderRadius.sm}};
  --brand-radius-md: {{borderRadius.md}};
  --brand-radius-lg: {{borderRadius.lg}};
  --brand-radius-full: {{borderRadius.full}};

  /* Transitions */
  --brand-transition-fast: {{transitions.fast}};
  --brand-transition-normal: {{transitions.normal}};
}
